/* function Slider() {
  const slidingNewsletter = document.querySelector('.slide-in')

  window.addEventListener('scroll', () => {
    const { scrollTop, clientHeight } = document.documentElement

    console.log(scrollTop, clientHeight)

    const topElementToTopViewport =
      slidingNewsletter.getBoundingClientReact().top

    console.log(topElementToTopViewport)

    if (
      scrollTop >
      (scrollTop + topElementToTopViewport).toFixed() - clientHeight * 0.5
    ) {
      slidingNewsletter.classList.add('active')
    }
    return
  })
}

export default Slider */
