import '../../styles/slideOutIn.css'

import { useEffect, useRef } from 'react'

function UseSlideInOut(ref) {
  const elementRef = useRef(null)
  UseSlideInOut(elementRef)
  useEffect(() => {
    function handleScroll() {
      const element = ref.current
      const elementRect = element.getBoundingClientRect()
      const elementInView =
        elementRect.top >= 0 && elementRect.bottom <= window.innerHeight

      if (elementInView) {
        element.classList.add('in-view')
      } else {
        element.classList.remove('in-view')
      }
    }

    window.addEventListener('scroll', handleScroll)

    return () => {
      ;<div ref={elementRef} className="slide-in-out">
        Contenu de l'élément à animer
      </div>
    }
  }, [ref])
}

export default UseSlideInOut
