function Scroll() {
  const titleScroll = document.querySelector('.titleQuiSuisJe')

  return window.addEventListener('scroll', () => {
    if (window.scrollY > 200) {
      titleScroll.classList.add('scroll')
    }
  })
}

export default Scroll
