import '../../styles/slideOutIn.css'

import { useEffect } from 'react'

function useSlideInOutLeft(refLeft) {
  useEffect(() => {
    function handleScrollLeft() {
      const elementLeft = refLeft.current
      const elementRectLeft = elementLeft.getBoundingClientRect()
      const elementInViewLeft =
        elementRectLeft.top >= 0 && elementRectLeft.bottom <= window.innerHeight

      if (elementInViewLeft) {
        elementLeft.classList.add('in-scroll')
      } else {
        elementLeft.classList.remove('in-scroll')
      }
    }

    window.addEventListener('scrollLeft', handleScrollLeft)

    return () => {
      window.removeEventListener('scrollLeft', handleScrollLeft)
    }
  }, [refLeft])
}

export default useSlideInOutLeft
