import React from 'react'
import './opaxity.css'

class WritingText extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      currentLetter: 0,
    }
  }

  componentDidMount() {
    this.interval = setInterval(() => {
      this.setState((prevState) => {
        return { currentLetter: prevState.currentLetter + 1 }
      })
    }, 500)
  }

  componentWillUnmount() {
    clearInterval(this.interval)
  }

  render() {
    return (
      <div>
        {this.props.text.split('').map((letter, index) => {
          return (
            <span
              key={index}
              className={`letter ${
                index === this.state.currentLetter ? 'show-letter' : ''
              }`}
            >
              {letter}
            </span>
          )
        })}
      </div>
    )
  }
}

export default WritingText
